import React from 'react';
import {
    useState
} from 'react';


function NodeComponent(props) {
    const [state, setState] = useState(true);

    function onToggle(e) {

        if (state == true) {
            setState(false)
        } else setState(true)
    }

    const dataModel = props.dataModel;
    if (state == true) {
        return <div >
            <
            div >
            <
            li > < h2 className = "nodeName"
        onClick = {
                onToggle
            } > < span className = "glyphicon glyphicon-chevron-down" > < /span> {dataModel.name}</h2 > < /li> <
            div className = "nodeChildren" > {
                dataModel.children.map((child) =>
                    <
                    NodeComponent dataModel = {
                        child
                    }
                    />)} <
                    /div>  <
                    /div> <
                    /div> } 
                    else {
                        return <div >
                            <
                            li > < h2 className = "nodeName"
                        onClick = {
                                onToggle
                            } > < span className = "glyphicon glyphicon-chevron-down" > < /span> {dataModel.name}</h2 > < /li> <
                            /div> }
                    }

                    export default NodeComponent;
